INSERT INTO `locales_source` VALUES (3093,'','default','OPML integration','','none');
INSERT INTO `locales_source` VALUES (3094,'','default','A <a href=\"@aggregator-opml\">machine-readable OPML file</a> of all feeds is available. OPML is an XML-based file format used to share outline-structured information such as a list of RSS feeds. Feeds can also be <a href=\"@import-opml\">imported via an OPML file</a>.','','none');
