INSERT INTO `locales_target` VALUES (3225,'Контекстные ссылки реализуются модулями и предоставляют быстрый доступ к задачам, связанным с объектом к которому они показываются. Например, если у вас есть добавленный в регион блок меню, то с этим блоком связаны модули Blocks и Menus, и для блока будут доступны ссылки для перехода к списку ссылок этого меню, перехода к правке ссылок этого меню и настройке параметров блока. Модуль Contextual links собирает эти ссылки в список, вывод которого зависит от используемой вами темы и также добавляет JavaScript, который позволяет изначально скрывать эти ссылки и показывать их только при наведении на объект к которому они относятся мышки.','ru',0,0,0);
