INSERT INTO `locales_target` VALUES (3351,'Модуль File позволяет прикреплять файлы (PDF, документы и т.д.) к содержанию, после того, как поле «Файл» добавлено к типу содержания используя модуль <a href=\"@fieldui-help\">Field UI</a>. К этому полю можно добавить различные проверки прикрепляемых файлов, например, ограничить максимальный размер файла или разрешённые расширения файлов (типы файлов).','ru',0,0,0);
