INSERT INTO `locales_target` VALUES (3422,'Дополнительную информацию можно найти по ссылкам, перечисленным ниже, либо в онлайновом <a href=\"@handbook\">руководстве по Drupal</a>. Вы можете также написать сообщение на <a href=\"@forum\">форуме оф.сайта Drupal</a> или использовать возможности  <a href=\"@support\">пользовательской поддержки</a> (англ.). \r\n<small>Прим. переводчика: существует <a href=\"http://drupal.ru\">сайт русскоязычного сообщества Drupal</a>. На нем можно получить дополнительную информацию, поддержку пользователей и адреса других русскоязычных ресурсов по Drupal.</small>','ru',0,0,0);
