INSERT INTO `locales_target` VALUES (3488,'Параметр «alt» позволяет ввести для изображения альтернативный текст. Этот текст используется устройствами чтения с экрана, поисковыми системами и показывается браузером вместо изображения в том случае, когда изображение не может быть загружено.','ru',0,0,0);
