INSERT INTO `locales_target` VALUES (3526,'Если используется префикс пути, то для URL вида http://example.com/ru/contact, будет использоваться русский язык. Если используется домен, то для URL вида http://de.example.com/contact, будет использоваться русский язык. Отметьте: изменение этого параметра может привести к тому, что существующие ссылки станут ссылаться на несуществующие адреса, так как изменение этого параметра приведёт к изменению URL страницы.','ru',0,0,0);
