INSERT INTO `locales_target` VALUES (3573,'Определите источник для меню дополнительных ссылок. Расширенные настройки позволяют использовать один и тот же источник для обоих меню: меню основных ссылок (используется %main) и меню дополнительных ссылок. Если источник меню имеет 2 уровня вложенности, ссылки верхнего уровня попадут в меню основных ссылок, а их дочерние - в меню дополнительных ссылок.','ru',0,0,0);
