INSERT INTO `locales_target` VALUES (3627,'Когда добавляется новый документ, то модуль Node записывает основную информацию о документе, включая автора, дату создания и <a href=\"@content-type\">тип документа</a>. Он также управляет параметрами публикации, которые определяют будет или нет документ опубликован, будет ли он выведен на первую страницу сайта и(или) закреплён вверху списков с документами. Параметры, которые будут использоваться по умолчанию, можно настроить отдельно для каждого <a href=\"@content-type\">типа документов</a>.','ru',0,0,0);
